--
-- Structure de la table `p5_ouvrage`
--

CREATE TABLE `p5_ouvrage` (
  `OUV_ID` int(11) NOT NULL,
  `OUV_TITRE` text NOT NULL,
  `OUV_SOUSTITRE` text NOT NULL,
  `OUV_PREFACE` longtext NOT NULL,
  `OUV_DESCRIPTION` text,
  `OUV_ENABLE` int(11) DEFAULT '0',
  `OUV_KEYWORDS` text,
  `OUV_IMAGE` varchar(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Déchargement des données de la table `p5_ouvrage`
--

INSERT INTO `p5_ouvrage` (`OUV_ID`, `OUV_TITRE`, `OUV_SOUSTITRE`, `OUV_PREFACE`, `OUV_DESCRIPTION`, `OUV_ENABLE`, `OUV_KEYWORDS`, `OUV_IMAGE`) VALUES
(12, 'L\'étranger aux yeux d\'émeraude', '', '<p>Dans le royaume d\'Ishyr, des rumeurs courent &agrave; propos d\'un petit village isol&eacute;... Certaines sont &eacute;tranges, d\'autres terrifiantes, parfois m&ecirc;me d&eacute;lirantes. Tandis que la cour n\'en a que faire, le roi tente de d&eacute;m&ecirc;ler le vrai du faux. Il re&ccedil;oit alors une visite qui lui apportera peut-&ecirc;tre des r&eacute;ponses... mais &agrave; quel prix ?</p>', 'Comme dans un bon film d\'horreur, la tension monte petit à petit pour finir d\'une manière très noire, digne de ce qu\'on pourrait attendre.', 1, ' vampire, lettres, derrek', 'ouvrage12.jpg'),
(13, 'Le dernier vol du papillon', '', '<p>Lorsqu\'on est chass&eacute; de son clan pour quelques centim&egrave;tres de plus ou pour des cheveux rouges, on peut penser &agrave; partir et &agrave; ne ne plus revenir en arri&egrave;re. Mais si notre peuple &eacute;tait en grand danger et que nous &eacute;tions les seuls capables de le sauver, aurions-nous l\'audace de faire demi-tour ?</p>\r\n<p>&nbsp;</p>\r\n<p>Imaginez un monde.<br />&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp; Dans ce monde, imaginez un oc&eacute;an.<br />&nbsp;&nbsp;&nbsp; Et dans cet oc&eacute;an, imaginez une &Icirc;le.<br />&nbsp;&nbsp;&nbsp; Seule. Unique.<br />&nbsp;&nbsp;&nbsp; Perdue.<br />&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp; Au nord de cette &icirc;le, imaginez un volcan, vomissant quotidiennement le sang de la terre, ainsi que trois peuples, trois races, partageant les lieux encore vivables.<br />&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp; Bienvenue dans notre Cosmos.<br />&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp; &Agrave; pr&eacute;sent, respirez notre air sulfureux ; voyez nos arbres, nos plaines et nos vall&eacute;es. Sentez le parfum de nos fleurs, go&ucirc;tez &agrave; la caresse de notre vent et &eacute;coutez le bruissement de nos feuilles.<br />&nbsp;&nbsp;&nbsp; Ce monde est aussi le v&ocirc;tre, &agrave; pr&eacute;sent.<br />&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp; Vous d&eacute;couvrirez bient&ocirc;t &agrave; quel point il est dangereux.</p>', 'Monde imaginaire , dangereux ', 1, 'blog,écrivain,volcan, technomagie, fantasy', 'ouvrage13.jpg');

